%% Load data, and apply projectionroot_save = '/media/harry/mri1/backup/ODT_conventional_tomo_db/';root_input_axi = [root_save 'Input_microbead_axi/20180726.150404.579.SiO2_5um-001/'];root_input_cor = [root_save 'Input_microbead_cor/20180726.150404.579.SiO2_5um-001/'];root_input_sag = [root_save 'Input_microbead_sag/20180726.150404.579.SiO2_5um-001/'];root_recon = '/media/harry/ExtDrive/ODT_conventional_tomo/results/';% method = 'tomo_patch192_g10_0730_bead/';method = 'tomo_patch128_g5_0805_bead/';root_recon = [root_recon method];root_recon_axi = [root_recon 'recon_microbead_axi/Input_microbead_axi/'];root_recon_cor = [root_recon 'recon_microbead_cor/Input_microbead_axi/'];root_recon_sag = [root_recon 'recon_microbead_sag/Input_microbead_axi/'];for i = 1:360    load([root_input_axi 'a' num2str(i)], 'proj');    axi_proj = proj;    load([root_input_cor 'a' num2str(i)], 'proj');    cor_proj = proj;    load([root_input_sag 'a' num2str(i)], 'proj');    sag_proj = proj;    load([root_recon_axi 'a' num2str(i)], 'recons');    axi_recons = recons;    load([root_recon_cor 'a' num2str(i)], 'recons');    cor_recons = recons;    load([root_recon_sag 'a' num2str(i)], 'recons');    sag_recons = recons;    figure(1);    subplot(231); imagesc(axi_proj);   colormap gray; axis off image;title(i);    subplot(232); imagesc(cor_proj);   colormap gray; axis off image;title(i);    subplot(233); imagesc(sag_proj);   colormap gray; axis off image;title(i);    subplot(234); imagesc(axi_recons); colormap gray; axis off image;    subplot(235); imagesc(cor_recons); colormap gray; axis off image;    subplot(236); imagesc(sag_recons); colormap gray; axis off image;%             pause();    drawnow();end